mapper.insert:
INSERT INTO AUCTION(AUCTIONNUM, REQUESTNUM, USERID, AUCTIONPRICE, AUCTIONDATE, AUCTIONSTATE
, AUCTIONCOMMENT
)
VALUES(SEQ_REQUESTBOARDREPLY.nextval, #{requestNum}, #{userId}, #{auctionPrice}, sysdate, 0
, #{auctionComment}
)

mapper.select:
SELECT * FROM AUCTION
WHERE REQUESTNUM = #{requestNum}
ORDER BY AUCTIONDATE DESC

mapper.update:
UPDATE AUCTION SET
auctionPrice = #{auctionPrice}
auctionComment = #{auctionComment}
WHERE auctionNum = #{auctionNum}

mapper.insert:
INSERT INTO REQUESTDEAL(DEALNUM, REQUESTNUM, DEALBUYERID, DEALSELLERID, DEALPRICE, DEALDATE
, DEALIMAGE
)
VALUES(SEQ_REQUESTBOARDDEAL.nextval, #{requestNum}, #{dealBuyerId}, #{dealSellerId}, #{dealPrice}, sysdate
, #{dealImage}
)

mapper.update:
UPDATE REQUESTBOARD
SET REQUESTSTATE = 1
where requestNum = #{requestNum}
